  {
    json: {
      title: "Machine Learning Assistant ‚Äî Karachi",
      description: "Earn up to 400,000 PKR weekly! Send your CV via WhatsApp to +923001234567. No experience required.",
      link: "https://fakejobsite.pk/ml"
    }
  },
  {
    json: {
      title: "AI Research Associate ‚Äî Islamabad",
      description: "Assist senior researchers in NLP projects. Requires basic understanding of neural networks.",
      link: "https://example.com/job/research"
    }
  }
];

const items = $input.all();
const results = [];

for (const item of items) {
  const raw = item.json;

  try {
    const text = raw.content?.parts?.[0]?.text || "";
    const jsonString = text.replace(/```json|```/g, "").trim();
    const parsed = JSON.parse(jsonString);

    // Combine authenticity, reason, and job details
    results.push({
      json: {
        authenticity: parsed.authenticity || "UNKNOWN",
        reason: parsed.reason || "No reason provided.",
        job: parsed.job || {
          title: "Untitled",
          company: "Unknown",
          location: "Unspecified",
          url: "No link",
          salary: "Not mentioned",
          description: "No description available."
        }
      }
    });
  } catch (err) {
    results.push({
      json: {
        authenticity: "UNKNOWN",
        reason: `Parsing error: ${err.message}`,
        job: {
          title: "Untitled",
          company: "Unknown",
          location: "Unspecified",
          url: "No link",
          salary: "Not mentioned",
          description: "No description available."
        }
      }
    });
  }
}

return results;

const items = $input.all();
const results = [];

for (const item of items) {
  const job = item.json.job || {};

  const title = job.title || "Untitled";
  const company = job.company || "Unknown";
  const location = job.location || "Unspecified";
  const link = job.url || "No link";
  const reason = item.json.reason || "No reason provided";
  const authenticity = (item.json.authenticity || "unknown").toUpperCase();

  // Create a Discord embed
  const embed = {
    title: `${title}`,
    url: link,
    color: authenticity === "REAL" ? 3066993 : authenticity === "FAKE" ? 15158332 : 9807270, // green, red, grey
    fields: [
      { name: "üè¢ Company", value: company, inline: true },
      { name: "üìç Location", value: location, inline: true },
      { name: "‚úÖ Authenticity", value: authenticity, inline: true },
      { name: "üìù Description", value: $input.first().json.job.description || "No description provided", inline: true },
      { name: "üßæ Reason", value: reason || "No reason provided", inline: false }
    ],
    footer: { text: "ü§ñ Verified by AI Job Checker" },
    timestamp: new Date().toISOString()
  };

  // Discord webhook expects a "content" or "embeds" array
  results.push({
    json: {
      content: "**üß† AI/ML Job Alert!**",
      embeds: [embed]
    }
  });
}

return results;
